<?php 
/**
 * Template:			pdf-reach.php
*/

if ( have_rows( 'reach_data' ) ) { while ( have_rows( 'reach_data') ) { the_row();
    $registration_no = get_sub_field( 'registration_number' );
    $tonnage_band = get_sub_field( 'tonnage_band_mt' );
}}
$product = get_the_title(); 
$content = get_field( 'pdf_reach', 'options' ); 
$content = str_replace( '%%product%%', $product, $content );
$content = str_replace( '%%registration%%', $registration_no, $content );
$content = str_replace( '%%tonnage%%', $tonnage_band, $content );


?>

<div class="pdf" id="pdf-reach">
    <img style="width:200px" alt="Partinchem logo" src="data:image/png;base64,">
    <br><br><br><br><br>
    <h2 style="font-size:32px;color:#2b3147"><strong>Regulation EC NËš 1907/2006</strong></h2>
    <h4 style="color:#2b3147"><?php the_title(); ?></h4>
    <hr>
    <p style="font-size:14px;color:#2b3147;">Date: <?php echo date("l, M j, Y"); ?></p>  
    <?php echo $content; ?>  
</div>